import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const [todos, setTodos] = useState([]);
    // NEW state for the form inputs
    const [text, setText] = useState('');
    const [priority, setPriority] = useState('Medium');
    const [dueDate, setDueDate] = useState('');

    // NEW state for editing
    const [editingId, setEditingId] = useState(null);
    const [editingText, setEditingText] = useState('');

    // NEW state for filtering
    const [filter, setFilter] = useState('All');

    const { logout } = useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('/api/todos')
            .then(res => setTodos(res.data))
            .catch(err => console.error(err));
    }, []);

    // MODIFIED to handle new fields
    const handleAddTodo = async (e) => {
        e.preventDefault();
        if(!text.trim()) return;
        try {
            const newTodo = { text, priority, dueDate: dueDate || null };
            const res = await axios.post('/api/todos', newTodo);
            setTodos([res.data, ...todos]);
            // Reset form
            setText('');
            setPriority('Medium');
            setDueDate('');
        } catch (err) {
            console.error(err);
        }
    };

    const toggleComplete = async (id, completed) => {
        try {
            const res = await axios.put(`/api/todos/${id}`, { completed: !completed });
            setTodos(todos.map(todo => (todo._id === id ? res.data : todo)));
        } catch (err) {
            console.error(err);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/todos/${id}`);
            setTodos(todos.filter(todo => todo._id !== id));
        } catch (err) {
            console.error(err);
        }
    };

    // NEW function to start editing a todo
    const handleStartEdit = (todo) => {
        setEditingId(todo._id);
        setEditingText(todo.text);
    };
    
    // NEW function to save an edited todo
    const handleSaveEdit = async (id) => {
        try {
            const res = await axios.put(`/api/todos/${id}`, { text: editingText });
            setTodos(todos.map(todo => (todo._id === id ? res.data : todo)));
            setEditingId(null);
            setEditingText('');
        } catch (err) {
            console.error(err);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    // NEW logic to filter todos before rendering
    const filteredTodos = todos.filter(todo => {
        if (filter === 'Active') return !todo.completed;
        if (filter === 'Completed') return todo.completed;
        return true; // 'All'
    });

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <h1>TaskMaster Pro</h1>
                <button onClick={handleLogout} className="logout-btn">Logout</button>
            </header>

            {/* MODIFIED Form with new fields */}
            <form onSubmit={handleAddTodo} className="todo-form">
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Add a new task..."
                    className="task-input"
                />
                <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                </select>
                <input
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                />
                <button type="submit">Add Task</button>
            </form>

            {/* NEW Filter buttons */}
            <div className="filter-buttons">
                <button onClick={() => setFilter('All')} className={filter === 'All' ? 'active' : ''}>All</button>
                <button onClick={() => setFilter('Active')} className={filter === 'Active' ? 'active' : ''}>Active</button>
                <button onClick={() => setFilter('Completed')} className={filter === 'Completed' ? 'active' : ''}>Completed</button>
            </div>

            <ul className="todo-list">
                {/* MODIFIED to map over filteredTodos */}
                {filteredTodos.map(todo => (
                    <li key={todo._id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
                        {editingId === todo._id ? (
                            // NEW Edit mode view
                            <div className="edit-view">
                                <input
                                    type="text"
                                    value={editingText}
                                    onChange={(e) => setEditingText(e.target.value)}
                                    className="edit-input"
                                />
                                <button onClick={() => handleSaveEdit(todo._id)} className="save-btn">Save</button>
                                <button onClick={() => setEditingId(null)} className="cancel-btn">Cancel</button>
                            </div>
                        ) : (
                            // Normal view
                            <>
                                <div className="task-details">
                                    <span onClick={() => toggleComplete(todo._id, todo.completed)} className="task-text">
                                        {todo.text}
                                    </span>
                                    {/* NEW Priority and Due Date display */}
                                    <div className="meta-data">
                                        <span className={`priority ${todo.priority.toLowerCase()}`}>{todo.priority}</span>
                                        {todo.dueDate && (
                                            <span className="due-date">
                                                Due: {new Date(todo.dueDate).toLocaleDateString()}
                                            </span>
                                        )}
                                    </div>
                                </div>
                                <div className="task-actions">
                                    <button onClick={() => handleStartEdit(todo)} className="edit-btn">Edit</button>
                                    <button onClick={() => handleDelete(todo._id)} className="delete-btn">Delete</button>
                                </div>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
