import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const [todos, setTodos] = useState([]);
    const [text, setText] = useState('');
    const { logout } = useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('/api/todos')
            .then(res => setTodos(res.data))
            .catch(err => console.error(err));
    }, []);

    const handleAddTodo = async (e) => {
        e.preventDefault();
        if(!text.trim()) return;
        try {
            const res = await axios.post('/api/todos', { text });
            setTodos([res.data, ...todos]);
            setText('');
        } catch (err) {
            console.error(err);
        }
    };

    const toggleComplete = async (id, completed) => {
        try {
            const res = await axios.put(`/api/todos/${id}`, { completed: !completed });
            setTodos(todos.map(todo => (todo._id === id ? res.data : todo)));
        } catch (err) {
            console.error(err);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/todos/${id}`);
            setTodos(todos.filter(todo => todo._id !== id));
        } catch (err) {
            console.error(err);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <h1>TaskMaster Pro</h1>
                <button onClick={handleLogout} className="logout-btn">Logout</button>
            </header>

            <form onSubmit={handleAddTodo} className="todo-form">
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Add a new task..."
                />
                <button type="submit">Add Task</button>
            </form>

            <ul className="todo-list">
                {todos.map(todo => (
                    <li key={todo._id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
                        <span onClick={() => toggleComplete(todo._id, todo.completed)}>
                            {todo.text}
                        </span>
                        <button onClick={() => handleDelete(todo._id)} className="delete-btn">Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
