import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

// You will need to add the CSS for the animations to your App.css file
// if it's not already there.
/*
@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}
.fade-in-up { animation: fadeIn 0.8s ease-in-out forwards; opacity: 0; }
.delay-100 { animation-delay: 0.1s; }
.delay-200 { animation-delay: 0.2s; }
.delay-300 { animation-delay: 0.3s; }
.delay-400 { animation-delay: 0.4s; }
*/

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const { login } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // IMPORTANT: Use your deployed backend URL here
            const backendUrl = 'https://taskmaster-pro-server.onrender.com'; // Replace with your actual URL
            const res = await axios.post(`${backendUrl}/api/auth/login`, { username, password });
            login(res.data.token);
            navigate('/dashboard');
        } catch (err) {
            console.error(err.response ? err.response.data : 'Login failed');
            alert('Invalid credentials');
        }
    };

    return (
        <div className="bg-slate-50 flex items-center justify-center min-h-screen p-4">
            {/* Main container */}
            <div className="w-full max-w-4xl bg-white rounded-2xl shadow-xl flex flex-col md:flex-row overflow-hidden">
                
                {/* Left Panel: Form Section */}
                <div className="w-full md:w-1/2 p-8 md:p-12 flex flex-col justify-center">
                    
                    {/* Header */}
                    <div className="text-left mb-8 fade-in-up">
                        <h2 className="text-3xl font-bold text-slate-800">Welcome Back!</h2>
                        <p className="text-slate-500 mt-2">Please log in to access your tasks.</p>
                    </div>

                    {/* Login Form */}
                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Username Input */}
                        <div className="fade-in-up delay-100">
                            <label htmlFor="username" className="block text-sm font-medium text-slate-600 mb-2">Username</label>
                            <input 
                                id="username" 
                                type="text" 
                                required 
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="w-full px-4 py-3 bg-slate-100 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
                                placeholder="e.g., vivek@123"
                            />
                        </div>
                        
                        {/* Password Input */}
                        <div className="fade-in-up delay-200">
                            <label htmlFor="password" className="block text-sm font-medium text-slate-600 mb-2">Password</label>
                            <input 
                                id="password" 
                                type="password" 
                                required 
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full px-4 py-3 bg-slate-100 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
                                placeholder="••••••••"
                            />
                        </div>

                        {/* Login Button */}
                        <div className="fade-in-up delay-300">
                            <button 
                                type="submit" 
                                className="w-full bg-indigo-500 text-white font-bold py-3 px-4 rounded-lg hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition transform hover:scale-105"
                            >
                                Login
                            </button>
                        </div>
                    </form>

                    {/* Footer Link */}
                    <div className="mt-8 text-center text-sm text-slate-500 fade-in-up delay-400">
                        <p>Don't have an account? 
                            <Link to="/register" className="font-medium text-indigo-500 hover:underline">Sign Up</Link>
                        </p>
                    </div>
                </div>

                {/* Right Panel: Image Section (Hidden on mobile) */}
                <div className="hidden md:block md:w-1/2 relative">
                    <img 
                        src="https://images.unsplash.com/photo-1521737604893-d14cc237f11d?q=80&w=1884&auto=format&fit=crop" 
                        alt="A team collaborating in a light, modern office" 
                        className="w-full h-full object-cover"
                        onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/600x800/e2e8f0/475569?text=Image'; }}
                    />
                    <div className="absolute inset-0 bg-white/20"></div>
                </div>
            </div>
        </div>
    );
};

export default Login;
